mapper.select:

        select @ROWNUM:=@ROWNUM+1 as rnum, A.*
		from (SELECT @ROWNUM:=0) B ,(SELECT * FROM product ORDER BY ${filter.quary_name} ${filter.order}) A
		where product_category = #{cate_num}
		limit #{paging.limit} offset #{paging.offset};

mapper.update:
  		UPDATE product
		SET product_quantity = product_quantity - #{minusQuantity}
		WHERE product_id = #{productId}
